// Countdown Timer
function countdown() {
  var now = new Date();
  var eventDate = new Date("2023-02-25 06:00:00"); // Change this date to your target date and time
  var currentTime = now.getTime();
  var eventTime = eventDate.getTime();

  var remTime = eventTime - currentTime;

  var s = Math.floor(remTime / 1000);
  var m = Math.floor(s / 60);
  var h = Math.floor(m / 60);
  var d = Math.floor(h / 24);

  h %= 24;
  m %= 60;
  s %= 60;

  h = (h < 10) ? "0" + h : h;
  m = (m < 10) ? "0" + m : m;
  s = (s < 10) ? "0" + s : s;

  document.getElementById("days").textContent = d;
  document.getElementById("hours").textContent = h;
  document.getElementById("minutes").textContent = m;
  document.getElementById("seconds").textContent = s;

  var progress = 100 - (remTime / (24 * 60 * 60 * 1000) * 100);
  document.getElementById("progress-bar").style.width = progress + "%";

  if (remTime < 0) {
    clearInterval(countdownInterval);
    document.getElementById("days").textContent = "06";
    document.getElementById("hours").textContent = "00";
    document.getElementById("minutes").textContent = "00";
    document.getElementById("seconds").textContent = "00";
    document.getElementById("progress-bar").style.width = "10%";
  }
}

var countdownInterval = setInterval(countdown, 1000);

// Animated Elements
var animateElements = document.querySelectorAll(".animate");

function animateOnScroll() {
  var windowHeight = window.innerHeight;
  var scrollPosition = window.scrollY;

  animateElements.forEach(function(element) {
    var elementPosition = element.getBoundingClientRect().top + scrollPosition;

    if (elementPosition < windowHeight + scrollPosition) {
      element.classList.add("animated");
    }
  });
}

window.addEventListener("scroll", animateOnScroll);

