const correctPassword = 'royaluipremiumdemobysamir';

// Change this to the duration of the maintenance period in minutes
const maintenanceDuration = 60;

// Get the current time and the end time of the maintenance period
const startTime = new Date();
const endTime = new Date(startTime.getTime() + maintenanceDuration * 60 * 1000);

// Update the countdown timer every second
setInterval(() => {
  const now = new Date();
  const timeLeft = Math.max(0, endTime.getTime() - now.getTime());
  const minutes = Math.floor(timeLeft / (60 * 1000));
  const seconds = Math.floor((timeLeft % (60 * 1000)) / 1000);
  const countdown = document.getElementById('countdown');
  countdown.innerHTML = `Site will be back online in ${minutes}m ${seconds}s`;
}, 1000);

function checkPassword() {
  const password = document.getElementById('password').value;
  if (password === correctPassword) {
    // Redirect to the limited version of the site
    window.location.href = 'https://royaluinewversion.blogspot.com/';
  } else {
    document.getElementById('alert').innerHTML = 'Incorrect password, please try again.';
    document.getElementById('password').value = '';
  }
}

// Show the alert when the site is back online
setTimeout(() => {
  document.querySelector('.popup').style.display = 'none';
  document.querySelector('.popup-alert').style.display = 'block';
  document.querySelector('.popup-alert').innerHTML = 'The site is now back online!';
}, maintenanceDuration * 60 * 1000);
