function generateGreeting() {
  var currentDate = new Date();
  var currentHour = currentDate.getHours();
  var greetingMessage;

  if (currentHour < 12) {
    greetingMessage = "Good Morning Sir!";
  } else if (currentHour < 18) {
    greetingMessage = "Good Afternoon Sir!";
  } else {
    greetingMessage = "Good Night Sir!";
  }

  return greetingMessage;
}
var greetingMessage = document.getElementById("greeting-message");

setInterval(function() {
  var newGreeting = generateGreeting();
  greetingMessage.innerHTML = newGreeting;
}, 5000);
